# Constants
$SCREEN_WIDTH = 240
$SCREEN_HEIGHT = 136
$SPRITE_OFFSET = 4

class Player
  def initialize
    @x = 8
    @y = 8
    @scale = 1
  end

  def move_player
    if btn(0)
      change_y(-1)
    elsif btn(1)
      change_y(1)
    end

    if btn(2)
      change_x(-1)
    elsif btn(3)
      change_x(1)
    end
  end

  def draw_player
    spr(2, $SCREEN_WIDTH / 2 - $SPRITE_OFFSET, $SCREEN_HEIGHT / 2 - $SPRITE_OFFSET)
  end

  def get_x
    @x
  end

  def set_x(new_value)
    @x = new_value
  end

  def change_x(new_value)
    @x += new_value
  end

  def get_y
    @y
  end

  def set_y(new_value)
    @y = new_value
  end

  def change_y(new_value)
    @y += new_value
  end
end

class Camera
  def initialize
    @x = 0
    @y = 0
  end

  def get_x
    @x
  end

  def set_x(new_value)
    @x = new_value
  end

  def change_x(new_value)
    @x += new_value
  end

  def get_y
    @y
  end

  def set_y(new_value)
    @y = new_value
  end

  def change_y(new_value)
    @y += new_value
  end
end

# Variables
$player = Player.new
$camera = Camera.new

def TIC
  # Clear the screen
  cls(5)

  # Make camera follow player
  $camera.set_x($SCREEN_WIDTH / 2 - $SPRITE_OFFSET - $player.get_x)
  $camera.set_y($SCREEN_HEIGHT / 2 - $SPRITE_OFFSET - $player.get_y)
  map(0, 0, 30, 17, $camera.get_x, $camera.get_y, 0)

  # Player Functions
  $player.move_player
  $player.draw_player
end

